import './App.css'
const App = () => {
  /**
   * 当点击+时,数字增大
   * 当点击-时,数字减小
   */
  const addHandler = () => {
    // 1.获取h1元素
    const h1 = document.querySelector('h1')
    // 2.获取h1元素的文本内容
    const num = parseInt(h1.innerText)
    // 3.将文本内容转换为数字
    h1.innerText = num + 1
  }

  const subHandler = () => {
    // 1.获取h1元素
    const h1 = document.querySelector('h1')
    // 2.获取h1元素的文本内容
    const num = parseInt(h1.innerText)
    // 3.将文本内容转换为数字
    h1.innerText = num - 1
  }

  return (
    <div className="app">
      <h1>1</h1>
      <button onClick={addHandler}>+</button>
      <button onClick={subHandler}>-</button>
    </div>
  )
}

export default App
