/* 日志的容器 */
import LogItem from './LogItem/LogItem'
import './Logs.css'
import Card from '../UI/Card/Card'

const Logs = (props) => {

    // 过滤数据，只显示某一年的数据
    let filterData = props.logsData.filter(item => item.date.getFullYear() === 2023)

    console.log(filterData)

    /**
     * 将数据放在JSX中
     */
    let logItemData = filterData.map((item, index) => (
        <LogItem
            onDelLog={() => props.onDelLog(index)}
            key={item.id}
            date={item.date}
            desc={item.desc}
            time={item.time}
        />
    ))

    if (logItemData.length === 0) {
        logItemData = <p className="no-logs">没有找到学习的日志！</p>
    }

    return (
        <Card className="logs">
            {/*引入年份的过滤组件*/}
            <LogFilter />
            {logItemData}

        </Card>
    )
}

export default Logs
