import './App.css'
const App = () => {
  // 创建一个变量存储数据
  let counter = 1
  /**
   * 当点击+时,数字增大
   * 当点击-时,数字减小
   */
  const addHandler = () => {
    counter++
    console.log(counter)
    /* const h1 = document.querySelector('h1')
    const num = parseInt(h1.innerText)
    h1.innerText = num + 1 */
  }

  const subHandler = () => {
    const h1 = document.querySelector('h1')
    const num = parseInt(h1.innerText)
    h1.innerText = num - 1
  }

  return (
    <div className="app">
      <h1>{counter}</h1>
      <button onClick={addHandler}>+</button>
      <button onClick={subHandler}>-</button>
    </div>
  )
}

export default App
