import './App.css'
const App = () => {
  /**
   * 获取原生的DOM对象
   *    1.可以使用传统的document来对DOM进行操作
   *    2.直接从React处获取DOM对象
   *      步骤:
   *        1.创建一个存储DOM对象的容器
   *          - 使用useRef()
   */
  const clickHandler = e => {
    // 通过id获取h1
    const header = document.getElementById('header')
    // alert(header.innerText)
    header.innerText = '学习一下React'
  }

  return (
    <div className={'app'}>
      <h1 id="header">我是标题</h1>
      <button onClick={clickHandler}>1</button>
      <button>2</button>
    </div>
  )
}

export default App
