import './App.css'
const App = () => {
  // 创建一个变量存储数据
  let counter = 1
  /**
   * 当点击+时,数字增大
   * 当点击-时,数字减小
   */
  const addHandler = () => {
    counter++
    console.log(counter)
  }

  const subHandler = () => {
    counter--
    console.log(counter)
  }

  return (
    <div className="app">
      <h1>{counter}</h1>
      <button onClick={addHandler}>+</button>
      <button onClick={subHandler}>-</button>
    </div>
  )
}

export default App
