import { useState } from 'react'
import './App.css'
const App = () => {
  console.log('函数执行了-- > 组件创建完毕!')
  /**
   * 在React中,当组件渲染完毕后,在修改组件中的变量,不会使组件重新渲染
   *    要使得组件可以受到变量的影响,必须在组件修改后对组件进行重新渲染
   *    这里我们就需要一个特殊的变量,当组件修改时,组件会自动重新渲染
   *
   * state相当于是一个变量,
   *    只是这个变量在React中进行了注册,
   *    React会监控这个变量的变化,当state发生变化的时候,会自动触发组件的重新渲染
   *    使得我们的修改可以在页面中进行呈现
   *
   * 在函数式组件中,我们需要通过钩子函数,获取state
   *    使用useState()函数,可以获取state
   *      import {useState} from 'react'
   *    它需要一个值作为参数,这个值就是state的初始值
   *      - 该函数会返回一个数组
   *        1.数组中的第一个元素就是初始值
   *        2.数组中的第二个元素就是一个函数,这个函数可以修改state的值
   */
  const result = useState(1)
  let counter = result[0]

  /**
   * 当点击+时,数字增大
   * 当点击-时,数字减小
   */

  // 创建一个变量存储数据
  // let counter = 1
  const addHandler = () => {
    counter++
    console.log(counter)
    /* const h1 = document.querySelector('h1')
    const num = parseInt(h1.innerText)
    h1.innerText = num + 1 */
  }

  const subHandler = () => {
    counter--
    console.log(counter)
    /* const h1 = document.querySelector('h1')
    const num = parseInt(h1.innerText)
    h1.innerText = num - 1 */
  }

  return (
    <div className="app">
      <h1>{counter}</h1>
      <button onClick={addHandler}>+</button>
      <button onClick={subHandler}>-</button>
    </div>
  )
}

export default App
