import React from 'react'
import { useParams } from 'react-router-dom'

const STU_DATA = [
    {
        id: 1,
        name: '张三'
    },
    {
        id: 2,
        name: '李四'
    },
    {
        id: 3,
        name: '王五'
    },
    {
        id: 4,
        name: '赵六'
    }
]
const Student = () => {

    // 可以使用useParams（）来获取参数
    const {id} = useParams()

    const stu = STU_DATA.find(item => item.id === +id)

    return (
        <div>
            <h2>{stu.id} --- {stu.name}</h2>
        </div>
    )
}

export default Student
